mapper.select:
SELECT * from ( ( SELECT IFNULL(count(t.id), 0) as namespaces from cloud_native_source t where t.source_type = 'Namespace'
and t.cloud_native_id like CONCAT('%', #{cloudNativeId},'%')
) as namespaces, ( SELECT IFNULL(count(t.id), 0) as pods from cloud_native_source t where t.source_type = 'Pod'
and t.cloud_native_id like CONCAT('%', #{cloudNativeId},'%')
) as pods, ( SELECT IFNULL(count(t.id), 0) as nodes from cloud_native_source t where t.source_type = 'Node'
and t.cloud_native_id like CONCAT('%', #{cloudNativeId},'%')
) as nodes, ( SELECT IFNULL(count(t.id), 0) as deployments from cloud_native_source t where t.source_type = 'Deployment'
and t.cloud_native_id like CONCAT('%', #{cloudNativeId},'%')
) as deployments, ( SELECT IFNULL(count(t.id), 0) as daemonsets from cloud_native_source t where t.source_type = 'Daemonset'
and t.cloud_native_id like CONCAT('%', #{cloudNativeId},'%')
) as daemonsets, ( SELECT IFNULL(count(t.id), 0) as services from cloud_native_source t where t.source_type = 'Service'
and t.cloud_native_id like CONCAT('%', #{cloudNativeId},'%')
) as services, ( SELECT IFNULL(count(t.id), 0) as ingress from cloud_native_source t where t.source_type = 'Ingress'
and t.cloud_native_id like CONCAT('%', #{cloudNativeId},'%')
) as ingress, ( SELECT IFNULL(count(t.id), 0) as roles from cloud_native_source t where t.source_type = 'Role'
and t.cloud_native_id like CONCAT('%', #{cloudNativeId},'%')
) as roles, ( SELECT IFNULL(count(t.id), 0) as secrets from cloud_native_source t where t.source_type = 'Secret'
and t.cloud_native_id like CONCAT('%', #{cloudNativeId},'%')
) as secrets, ( SELECT IFNULL(count(t.id), 0) as configmaps from cloud_native_source t where t.source_type = 'Configmap'
and t.cloud_native_id like CONCAT('%', #{cloudNativeId},'%')
) as configmaps, ( SELECT IFNULL(count(t.id), 0) as statefulSets from cloud_native_source t where t.source_type = 'StatefulSet'
and t.cloud_native_id like CONCAT('%', #{cloudNativeId},'%')
) as statefulSets, ( SELECT IFNULL(count(t.id), 0) as cronJobs from cloud_native_source t where t.source_type = 'CronJob'
and t.cloud_native_id like CONCAT('%', #{cloudNativeId},'%')
) as cronJobs, ( SELECT IFNULL(count(t.id), 0) as jobs from cloud_native_source t where t.source_type = 'Job'
and t.cloud_native_id like CONCAT('%', #{cloudNativeId},'%')
) as jobs, ( SELECT IFNULL(count(t.id), 0) as pvs from cloud_native_source t where t.source_type = 'PV'
and t.cloud_native_id like CONCAT('%', #{cloudNativeId},'%')
) as pvs, ( SELECT IFNULL(count(t.id), 0) as pvcs from cloud_native_source t where t.source_type = 'PVC'
and t.cloud_native_id like CONCAT('%', #{cloudNativeId},'%')
) as pvcs, ( SELECT IFNULL(count(t.id), 0) as leases from cloud_native_source t where t.source_type = 'Lease'
and t.cloud_native_id like CONCAT('%', #{cloudNativeId},'%')
) as leases, ( SELECT IFNULL(count(t.id), 0) as endpointSlices from cloud_native_source t where t.source_type = 'EndpointSlice'
and t.cloud_native_id like CONCAT('%', #{cloudNativeId},'%')
) as endpointSlices, ( SELECT IFNULL(count(t.id), 0) as events from cloud_native_source t where t.source_type = 'Event'
and t.cloud_native_id like CONCAT('%', #{cloudNativeId},'%')
) as events, ( SELECT IFNULL(count(t.id), 0) as networkPolicies from cloud_native_source t where t.source_type = 'NetworkPolicy'
and t.cloud_native_id like CONCAT('%', #{cloudNativeId},'%')
) as networkPolicies, ( SELECT IFNULL(count(t.id), 0) as versions from cloud_native_source t where t.source_type = 'Version'
and t.cloud_native_id like CONCAT('%', #{cloudNativeId},'%')
) as versions )
