mapper.select:
select IFNULL(t4.name, IFNULL(t3.name, IFNULL(t2.server_name, t1.name))) as name from history_scan t left join cloud_account t1 on t.account_id = t1.id left join server_result t2 on t.account_id = t2.id left join image_result t3 on t.account_id = t3.id left join package_result t4 on t.account_id = t4.id
AND DATE_SUB(CURDATE(), INTERVAL #{cycle} DAY) <= from_unixtime(t.create_time/1000, '%Y-%m-%d')
AND t.account_type in
#{item}
AND t.operator in
#{item}
AND t1.name is not null AND t2.server_name is not null AND t3.name is not null AND t4.name is not null
limit 30;

