AND DATE_SUB(CURDATE(), INTERVAL #{cycle} DAY) <= from_unixtime(t.create_time/1000, '%Y-%m-%d')
AND t.account_type in
#{item}
AND t.operator in
#{item}
AND t1.name is not null AND t2.server_name is not null AND t3.name is not null AND t4.name is not null
limit 30;
